// ViewTask.js

import React, { useState, useEffect } from 'react';
import '../Styles/view-task.css'; // Import your CSS file
import { useNavigate } from 'react-router-dom';

function ViewTask() {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Implement your logout logic here
    navigate('/login');
  };

  // Define state variables for tasks and filters
  const [tasks, setTasks] = useState([]);
  const [titleFilter, setTitleFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('none'); // Set default filter to "None"

  // Fetch tasks from the API on component mount
  useEffect(() => {
    // Fetch tasks from your API here
    // Update the tasks state with the fetched data
  }, []);

  // Handle task editing (you'll need to implement this)
  const handleEditTask = (taskId) => {
    // Implement task editing logic here
  };

  // Handle task deletion (you'll need to implement this)
  const handleDeleteTask = (taskId) => {
    // Implement task deletion logic here
  };

  // Filter tasks based on title and status
const filteredTasks = tasks.filter((task) => {
    const titleMatch = titleFilter === '' || task.title.toLowerCase().includes(titleFilter.toLowerCase());
    const statusMatch = statusFilter === 'none' || task.status === statusFilter;
    return titleMatch && statusMatch;
  });
  

  return (
    <div className="view-task-container">
      <button className="logout-button" onClick={handleLogout}>
        Logout
      </button>
      <h1 className="header">View Your Tasks</h1>
      <div className="filter-options">
        <input
          type="text"
          id="searchTitle"
          placeholder="Search by title"
          value={titleFilter}
          onChange={(e) => setTitleFilter(e.target.value)}
        />
        <select
          id="filterStatus"
          value={statusFilter}
          onChange={(e) => setStatusFilter(e.target.value)}
        >
          <option value="none">None</option> {/* Set "None" as the default filter */}
          <option value="pending">Pending</option>
          <option value="completed">Completed</option>
        </select>
      </div>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Due Date</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredTasks.map((task) => (
            <tr key={task.id}>
              <td>{task.title}</td>
              <td>{task.description}</td>
              <td>{task.dueDate}</td>
              <td>{task.status}</td>
              <td>
                <button onClick={() => handleEditTask(task.id)}>Edit</button>
                <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ViewTask;
