import React, { useState, useEffect } from 'react';
import '../Styles/todo.css';
import { useNavigate } from 'react-router-dom';

function Todo() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [status, setStatus] = useState('pending');
  const [searchTitle, setSearchTitle] = useState('');
  const [filterStatus, setFilterStatus] = useState('');
  const navigate = useNavigate();

  const handleLogout = () => {
    // Implement your logout logic here
    navigate('/login');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Create a new task object with user input and user ID from local storage
    const newTask = {
      userId: localStorage.getItem('userId'), // Get the userId from local storage
      title,
      description,
      dueDate,
      status,
    };

    // Send a request to your backend API to create the task
    try {
      const response = await fetch('your-create-task-api-endpoint', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newTask),
      });

      if (response.ok) {
        // Task creation was successful, refresh the task list
                // Clear the form fields
        setTitle('');
        setDescription('');
        setDueDate('');
        setStatus('pending');
      } else {
        // Task creation failed, handle the error
        console.error('Task creation failed');
      }
    } catch (error) {
      console.error('Error during task creation:', error);
    }
  };

    useEffect(() => {
    // Fetch the initial task list when the component mounts
    fetchTaskList();
  }, []);

  return (
    <div className="todo-container">
      <button className="logout-button" onClick={handleLogout}>
        Logout
      </button>
      <h1 className="header">Todo List</h1>
      <div className="task-form">
        <h2>Add Task</h2>
        <form onSubmit={handleSubmit}>
          {/* Input fields for task */}
          {/* ... */}
          <button type="submit">Add Task</button>
        </form>
      </div>
      <div className="task-list">
        <h2>Task List</h2>
        <div className="filter-options">
          {/* Filter and search input fields */}
          {/* ... */}
        </div>
        <table>
          <thead>
            <tr>
              <th>Title</th>
              <th>Description</th>
              <th>Due Date</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((task) => (
              <tr key={task.taskId}>
                {/* Display task details */}
                {/* ... */}
                <td>
                  <button>Edit</button>
                  <button>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Todo;
