import React, { useState, useEffect } from 'react';
import '../Styles/view-task.css'; // Import your CSS file
import { useNavigate } from 'react-router-dom';

function ViewTask() {
  const navigate = useNavigate();

  const handleLogout = () => {
    navigate('/login');
  };

  // Define state variables for tasks and filters
  const [tasks, setTasks] = useState([]);
  const [titleFilter, setTitleFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('none'); // Set default filter to "None"

  useEffect(() => {
    // Define a function to fetch all tasks
    const fetchAllTasks = async () => {
      try {
        const response = await fetch('https://your-api-url-here/view-all-tasks', {
          method: 'GET',
          headers: {
            // Add headers as needed
          },
        });

        if (response.ok) {
          const data = await response.json();
          setTasks(data.tasks); // Update tasks with the fetched data
        } else {
          console.error('Failed to fetch tasks');
        }
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    // Fetch all tasks only when titleFilter is empty and statusFilter is "none"
    if (titleFilter === '' && statusFilter === 'none') {
      fetchAllTasks();
    }
  }, [titleFilter, statusFilter]);
  
  const handleEditTask = (taskId) => {
    };

 const handleDeleteTask = (taskId) => {  
    };

  // Filter tasks based on title and status
const filteredTasks = tasks.filter((task) => {
    const titleMatch = titleFilter === '' || task.title.toLowerCase().includes(titleFilter.toLowerCase());
    const statusMatch = statusFilter === 'none' || task.status === statusFilter;
    return titleMatch && statusMatch;
  });
  

  return (
    <div className="view-task-container">
      <button className="logout-button" onClick={handleLogout}>
        Logout
      </button>
      <h1 className="header">View Your Tasks</h1>
      <div className="filter-options">
        <input
          type="text"
          id="searchTitle"
          placeholder="Search by title"
          value={titleFilter}
          onChange={(e) => setTitleFilter(e.target.value)}
        />
        <select
          id="filterStatus"
          value={statusFilter}
          onChange={(e) => setStatusFilter(e.target.value)}
        >
          <option value="none">None</option> {/* Set "None" as the default filter */}
          <option value="pending">Pending</option>
          <option value="completed">Completed</option>
        </select>
      </div>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Due Date</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredTasks.map((task) => (
            <tr key={task.id}>
              <td>{task.title}</td>
              <td>{task.description}</td>
              <td>{task.dueDate}</td>
              <td>{task.status}</td>
              <td>
                <button onClick={() => handleEditTask(task.id)}>Edit</button>
                <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ViewTask;
