import React, { useState } from 'react';
import '../Styles/userRegistration.css'; // Import your CSS file

function RegistrationForm() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');

  const validateEmail = (email) => {
    // Regular expression for a basic email format validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validatePassword = (password) => {
    // Check if password is at least 8 characters long
    const isLengthValid = password.length >= 8;

    // Check if password contains at least one uppercase letter
    const hasUppercase = /[A-Z]/.test(password);

    // Check if password contains at least one numeric character
    const hasNumeric = /\d/.test(password);

    return isLengthValid && hasUppercase && hasNumeric;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Reset error messages
    setEmailError('');
    setPasswordError('');

    // Validate email
    if (!validateEmail(email)) {
      setEmailError('Invalid email format');
      return;
    }

    // Validate password
    if (!validatePassword(password)) {
      setPasswordError('Invalid password format');
      return;
    }

    // If validation passes, proceed with form submission logic
    // Add your form submission logic here
  };  // Add form submission logic here
  return (
    <div className="registration-container">
    
      <div className="registration-form">
        <h2>Register to start</h2>
        <form>
          <div className="form-group">
            <label htmlFor="name">Name:</label>
            <input
              id="name"
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="username">Username:</label>
            <input
              id="username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button type="submit" className="registration-button">
            Register
          </button>
        </form>
      </div>
    </div>
  );
}
  

export default RegistrationForm;
