import React, { useState } from 'react';
import './Todo.css';

function Todo() {
  const [selectedView, setSelectedView] = useState('Create Task');

  return (
    <div className="todo-container">
      <h1 className="header">Todo List</h1>
      <div className="view-selector">
        <label htmlFor="viewSelect">Select View:</label>
        <select
          id="viewSelect"
          value={selectedView}
          onChange={(e) => setSelectedView(e.target.value)}
        >
          <option value="Create Task">Create Task</option>
          <option value="Update Task">Update Task</option>
          <option value="View All Tasks">View All Tasks</option>
          <option value="View Tasks by Filter">View Tasks by Filter</option>
        </select>
      </div>
      {selectedView === 'Create Task' && (
        <div className="task-form">
          <h2>Create Task</h2>
          {/* Create Task Form Goes Here */}
        </div>
      )}
      {selectedView === 'Update Task' && (
        <div className="task-form">
          <h2>Update Task</h2>
          {/* Update Task Form Goes Here */}
        </div>
      )}
      {selectedView === 'View All Tasks' && (
        <div className="task-list">
          <h2>View All Tasks</h2>
          {/* Task List Component Goes Here */}
        </div>
      )}
      {selectedView === 'View Tasks by Filter' && (
        <div className="filter-tasks">
          <h2>View Tasks by Filter</h2>
          {/* Filter Options and Task List Component Goes Here */}
        </div>
      )}
    </div>
  );
}

export default Todo;
