import React, { useState, useEffect } from 'react';
import '../Styles/view-task.css'; // Import your CSS file
import { useNavigate } from 'react-router-dom';

function ViewTask() {
  const navigate = useNavigate();

  const handleLogout = () => {
    navigate('/login');
  };

  // Define state variables for tasks and filters
  const [tasks, setTasks] = useState([]);
  const [titleFilter, setTitleFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('none'); // Set default filter to "None"

  useEffect(() => {
    // Define a function to fetch all tasks
    const fetchAllTasks = async () => {
      console.log("fetch all task strated");  
      const userId = localStorage.getItem('userId');
        console.log(userId);
        try {
        const response = await fetch(`https://ing0p7y9va.execute-api.us-east-1.amazonaws.com/get-user-tasks?userId=${userId}`, {
        method: 'GET',
        headers: {
        'Content-Type': 'application/json', // Set the content type as needed
    // Add any other headers your API requires
        }
});
        console.log(response);
        if (response.ok) {
          console.log('response is perfect');
          const data = await response.json();
          console.log('data:',data)
          setTasks(data.tasks); 
          console.log("Tasks:",data.tasks)// Update tasks with the fetched data
        } else {
          console.error('Failed to fetch tasks');
        }
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    // Fetch all tasks only when titleFilter is empty and statusFilter is "none"
    if (titleFilter === '' && statusFilter === 'none') {
      console.log('calling fetch all task method');
      fetchAllTasks();
    }
  }, [statusFilter]);

  useEffect(() => {
    console.log('second use effect started ');
    // Fetch tasks from your API when statusFilter changes
    const fetchTasks = async () => {
      try {
        console.log('fetch task method started');
        // Retrieve the userId from local storage
        const userId = localStorage.getItem('userId');
  
        const response = await fetch(`https://ing0p7y9va.execute-api.us-east-1.amazonaws.com/getTaskByStatus?userId=${userId}&taskstatus=${statusFilter}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });
  
        if (response.ok) {
          console.log('success');
          const data = await response.json();
          setTasks(data.tasks);
        } else {
          console.error('Error fetching tasks:', response.status);
        }
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };
    if (statusFilter !== 'none'){
    fetchTasks();
    }
  }, [statusFilter]);
  
  const handleEditTask = (taskId) => {
    };

 const handleDeleteTask = (taskId) => {  
    };

  
  

  return (
    <div className="view-task-container">
      <button className="logout-button" onClick={handleLogout}>
        Logout
      </button>
      <h1 className="header">View Your Tasks</h1>
      <div className="filter-options">
        <input
          type="text"
          id="searchTitle"
          placeholder="Search by title"
          value={titleFilter}
          onChange={(e) => setTitleFilter(e.target.value)}
        />
        <select
          id="filterStatus"
          value={statusFilter}
          onChange={(e) => setStatusFilter(e.target.value)}
        >
          <option value="none">None</option> {/* Set "None" as the default filter */}
          <option value="pending">Pending</option>
          <option value="completed">Completed</option>
        </select>
      </div>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Due Date</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.taskId}>
              <td>{task.title}</td>
              <td>{task.description}</td>
              <td>{task.dueDate}</td>
              <td>{task.status}</td>
              <td>
                <button onClick={() => handleEditTask(task.taskId)}>Edit</button>
                <button onClick={() => handleDeleteTask(task.taskId)}>Delete</button>
              </td>
            </tr>
          
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ViewTask;
